INSERT INTO `items` (`id`, `pid`, `name`, `description`, `class`, `podclass`, `pic`, `Weight`, `Cost`) VALUES
(1, 'PID_LEATHER_ARMOR', 'Кожаная броня', 'Ваша основная одежда из кожи. Искусно изготовлена из выделанной шкуры брамина.', 'Броня', '', 'ltharmor', 3628, 700),
(2, 'PID_METAL_ARMOR', 'Металлическая броня', 'Броня, грубо сформированная из полированных металлических пластин.', 'Броня', '', 'mtlarmor', 15875, 1100),
(3, 'PID_POWERED_ARMOR', 'Силовая броня', 'Высокотехнологичная автономная броня. Питается от ядерного микрореактора с запасом топлива лет на сто.', 'Топ', '', 'pwrarmor', 38555, 125000),
(4, 'PID_KNIFE', 'Нож', 'Острое колюще-режущее оружие. Мин. сила: 2.', 'Оружие', 'Холодное', 'oknife', 453, 40),
(5, 'PID_CLUB', 'Дубинка', 'Военная или полицейская дубинка из крепкого дерева. Мин. сила: 3.', 'Оружие', 'Холодное', 'club', 1360, 30),
(6, 'PID_SLEDGEHAMMER', 'Кувалда', 'Большой молот с массивной рукоятью. Очень популярное оружие среди мускулистых типов. Мин. сила: 6.', 'Оружие', 'Холодное', 'sledge', 5443, 120),
(7, 'PID_SPEAR', 'Копье', 'Заточенное до бритвенной остроты оружие. Древко деревянное, а наконечник изготовлен из кованой стали. Мин. сила: 4.', 'Оружие', 'Холодное', 'spear', 1814, 80),
(8, 'PID_10MM_PISTOL', 'Пистолет 10 мм', 'Самозарядный пистолет Кольт 6520 калибра 10 мм. При каждом нажатии на курок оружие автоматически перезаряжается, пока магазин не опустеет. Только одиночные выстрелы. Используются мощные 10-мм патроны. Мин. сила: 3.', 'Оружие', 'Легкое', 'pistol', 1360, 250),
(9, 'PID_10MM_SMG', 'Пистолет-пулемет 10 мм', 'Пистолет-пулемет H&K MP9 (модель калибра 10 мм). ПП среднего размера с возможностью одиночного выстрела и стрельбы очередями. Мин. сила: 4.', 'Оружие', 'Легкое', 'uzi', 2267, 1000),
(10, 'PID_HUNTING_RIFLE', 'Охотничье ружье', 'Полуавтоматическое ружье Кольт Рейнджмастер калибра 0,223. Только одиночные выстрелы. Мин. сила: 5.', 'Оружие', 'Легкое', 'hrifle', 4082, 1000),
(11, 'PID_FLAMER', 'Огнемет', 'Огнемет модели Факел-450. Выстреливает короткими струями невероятно жарко горящей жидкости. Для функционирования необходимо специальное топливо. Мин. сила: 6.', 'Оружие', 'Тяжелое', 'flamethr', 8164, 2000),
(12, 'PID_MINIGUN', 'Миниган', 'Персональный миниган CZ53 Роквелла. Многоствольный пулемет, стреляющий 5-мм патронами со скоростью свыше 60000 выстрелов в минуту. Мин. сила: 7.', 'Оружие', 'Тяжелое', 'minigun', 12700, 3800),
(13, 'PID_ROCKET_LAUNCHER', 'Гранатомет', 'Гранатомет Роквелла \"Супербазука\". Оснащен шикарным трехфунтовым пусковым механизмом. Стреляет бронебойными или разрывными ракетами. Мин. сила: 6.', 'Оружие', 'Тяжелое', 'rlaunch', 6803, 2300),
(14, 'PID_EXPLOSIVE_ROCKET', 'Разрывная ракета', 'Ракета, оснащенная мощной разрывной боеголовкой. ', 'Боеприпасы', '', 'shell2', 1360, 200),
(15, 'PID_PLASMA_RIFLE', 'Плазменное ружье', 'Плазменное ружье Винчестер P94. Энергетическое оружие индустриальной эпохи, стреляющее нагретой до предела плазмой через сверхпроводящий ствол. Питание осуществляется при помощи ядерных микрореакторов. Мин. сила: 6. Усиление: 15.', 'Оружие', 'Энерго', 'plasma', 5443, 4000),
(16, 'PID_LASER_PISTOL', 'Лазерный пистолет', 'Лазерный пистолет 1000-Ватт. Гражданская модель - мощность снижена по сравнению с военными или полицейскими моделями. Использует малые батарейки. Мин. сила: 3.', 'Оружие', 'Энерго', 'lasergun', 1814, 1400),
(17, 'PID_COMBAT_ARMOR', 'Боевая броня', 'Высокотехнологичная броня, изготовленная из улучшенных защитных полимеров.', 'Броня', '', 'cbtarmor', 8071, 6500),
(18, 'PID_DESERT_EAGLE', 'Дезерт Игл 0,44', 'Древний Дезерт Игл калибра 0,44 Магнум. Благодаря художественным фильмам конца ХХ века стал самым популярным пистолетом всех времен и народов. Мин. сила: 4.', 'Оружие', 'Легкое', 'magnum', 1814, 800),
(19, 'PID_ROCK', 'Камень', 'Это камень. Модель корпорации \"Гранит\" является усовершенствованной версией. Мин. сила: 1.', 'Оружие', 'Метательное', 'rock', 453, 1),
(20, 'PID_CROWBAR', 'Монтировка', 'Очень прочный и тяжелый металлический предмет, специально разработанный для применения принципа рычага. Или для хорошего удара по чьей-нибудь голове. Мин. сила: 5.', 'Оружие', 'Холодное', 'crowbar', 2267, 65),
(21, 'PID_BRASS_KNUCKLES', 'Кастет', 'Прочный кастет, изготовленный из стали. Защищает руку и обеспечивает больший урон при рукопашном бое. Мин. сила: 1.', 'Оружие', 'Рукопашное', 'brsnuck', 453, 40),
(22, 'PID_14MM_PISTOL', 'Пистолет 14 мм', 'Автоматический пистолет ЗИГ-Зауэр калибра 14 мм. Крупное, однозарядное оружие отличного качества. Мин. сила: 4.', 'Оружие', 'Легкое', 'smgpstle', 1814, 1100),
(23, 'PID_ASSAULT_RIFLE', 'Штурмовая винтовка', 'Штурмовая винтовка AK-112 калибра 5 мм. Старая военная модель, вышедшая из употребления примерно во время войны. Может производить одиночные выстрелы или стрелять очередью. Использует высокоскоростные заряды калибра 5 мм. Мин. сила: 5.', 'Оружие', 'Легкое', 'grifle', 3175, 1300),
(24, 'PID_PLASMA_PISTOL', 'Плазменный пистолет', 'Плазменный пистолет Глок 86. Разработан ИИ Гастона Глока. Выстреливает небольшим зарядом нагретой до предела плазмы. Питание осуществляется при помощи малых батареек. Мин. сила: 4. Усиление: 15.', 'Оружие', 'Энерго', 'glock', 1814, 2750),
(25, 'PID_FRAG_GRENADE', 'Осколочная граната', 'Обыкновенная осколочная граната. Содержит небольшое количество бризантного взрывчатого вещества. Большая часть опасных осколков формируется непосредственно из оболочки гранаты. Взрывается при ударе. Мин. сила: 3.', 'Оружие', 'Метательное', 'grenadeo', 453, 150),
(26, 'PID_PLASMA_GRENADE', 'Плазменная граната', 'Граната, в которой запечатана уплотненная при помощи магнитных полей плазма. Детонирует при помощи взрывчатого вещества. При ударе возникает взрыв перегретой плазмы. Мин. сила: 4.', 'Оружие', 'Метательное', 'grenadep', 453, 300),
(27, 'PID_PULSE_GRENADE', 'Импульсная граната', 'Граната электромагнитного импульса, при взрыве генерирующая мощное магнитное поле. На живые существа не действует. Поле возникает при ударе. Мин. сила: 4.', 'Оружие', 'Метательное', 'grenadeq', 453, 250),
(28, 'PID_GATLING_LASER', 'Гатлинг-лазер', 'Гатлинг-лазер H&K L30. В начале войны представлял собой прототип. Благодаря нескольким стволам некоторое время удается избежать перегрева. Питание осуществляется при помощи ядерных микрореакторов. Мин. сила: 6. Усиление: 30.', 'Топ', '', 'minilazr', 10886, 55500),
(29, 'PID_10MM_JHP', '10 мм п/об', 'Вид боеприпасов. Калибр: 10 мм, полуоболоченые.', 'Боеприпасы', '', '10mmjhp', 18, 3),
(30, 'PID_10MM_AP', '10 мм ББ', 'Вид боеприпасов. Калибр: 10 мм, бронебойные.', 'Боеприпасы', '', '10mmap', 23, 5),
(31, 'PID_44_MAGNUM_JHP', '0,44 Магнум п/об', 'Обойма патронов, калибр 0,44 Магнум, полуоболоченные.', 'Боеприпасы', '', '44jhp', 21, 4),
(32, 'PID_FLAMETHROWER_FUEL', 'Топливо для огнемета', 'Цилиндр, в котором содержится легковоспламеняемое жидкое топливо для огнеметов.', 'Боеприпасы', '', 'tank', 453, 25),
(33, 'PID_14MM_AP', '14 мм ББ', 'Боеприпасы крупного калибра. 14 мм, бронебойные.', 'Боеприпасы', '', '14mmap', 15, 5),
(34, 'PID_223_FMJ', '0,223 об.', 'Магазин с пулями, калибр 0,223, оболоченные.', 'Боеприпасы', '', '223fmj', 18, 4),
(35, 'PID_5MM_JHP', '5 мм п/об', 'Обойма небольших легких пуль. Калибр: 5 мм, полуоболоченные.', 'Боеприпасы', '', '5mmjhp', 11, 2),
(36, 'PID_5MM_AP', '5 мм ББ', 'Обойма малокалиберных пуль. 5 мм, бронебойные.', 'Боеприпасы', '', '5mmap', 16, 4),
(37, 'PID_ROCKET_AP', 'Ракета (ББ)', 'Снаряд, обладающий меньшей взрывной силой, но разработанный с целью пробивать броню.', 'Боеприпасы', '', 'rshell', 1410, 400),
(38, 'PID_SMALL_ENERGY_CELL', 'Мал. батарейка', 'Небольшая автономная единица хранения энергии.', 'Боеприпасы', '', 'ccells', 34, 10),
(39, 'PID_MICRO_FUSION_CELL', 'Яд. микрореактор', 'Средняя по величине единица производства энергии. Автономный ядерный реактор.', 'Боеприпасы', '', 'dcells', 45, 20),
(40, 'PID_STIMPAK', 'Стимулятор', 'Лекарственное вещество. При инъекции это вещество обеспечивает немедленное исцеление небольших ран.', 'Медикаменты', '', 'stimx', 0, 25),
(41, 'PID_BOTTLE_CAPS', 'Деньги', 'Законное платежное средство в мире пустошей.', 'Разное', '', 'cap2', 1, 1),
(45, 'PID_THROWING_KNIFE', 'Метательный нож', 'Нож, специально сбалансированный для метания. Изготовлен из титана, с лазерной заточкой. Мин. сила: 3.', 'Оружие', 'Метательное', 'knife2', 453, 100),
(46, 'PID_BAG', 'Сумка', 'Сумка средних размеров. Соткана из волоса браминов.', 'Container', '', NULL, NULL, NULL),
(47, 'PID_FIRST_AID_KIT', 'Аптечка первой помощи', 'Небольшой набор основного медицинского оборудования. Бинты, повязки, антисептический аэрозоль и многое другое.', 'Доп слот', '', '1staid', 907, 200),
(48, 'PID_RADAWAY', 'Антирадин', 'Химический раствор, молекулы которого связывают радиоактивные частицы и выводят их из организма. Для полного эффекта требует времени.', 'Медикаменты', '', 'iv', 0, 500),
(49, 'PID_ANTIDOTE', 'Противоядие', 'Бутылка с самодельным противоядием. Молочная жидкость с плавающими в ней кусочками мяса радскорпионов.', 'Медикаменты', '', 'drug_bot', 200, 50),
(50, 'PID_STEEL', 'Кусок стали', 'Вы совершенно не имеете понятие что это, но догадываетесь, что из этого можно что-нибудь сделать.', 'Разное', '', 'steel', 907, 20),
(51, 'PID_DYNAMITE', 'Динамит', 'Бризантное взрывчатое вещество, состоящее из нитроглицерина, смешанного c абсорбирующим веществом \"киселгур\".  Оснащено таймером.', 'Разное', '', 'dynamit', 2267, 500),
(52, 'PID_GEIGER_COUNTER', 'Счетчик Гейгера', 'Счетчик Гейгера от компании Ватт Электроникс, модель \"Радомер\". Фиксирует наличие и мощность радиационных полей.', 'Разное', '', 'geiger4', 1814, 650),
(53, 'PID_MENTATS', 'Ментат', 'Таблетки из химического вещества, расширяющего сознание. Улучшает функции головного мозга, связанные с памятью, а также убыстряет некоторые умственные процессы.  Вызывает сильное привыкание.', 'Медикаменты', '', 'syring2', 0, 480),
(54, 'PID_STEALTH_BOY', 'Невидимка', 'Персональное маскировочное устройство \"Невидимка 3001\" от компании Роб. Генерирует модулирующее поле, которое передает отраженный свет с одной стороны объекта на противоположную.', 'Доп слот', '', 'stlthboy', 907, 1800),
(55, 'PID_WATER_CHIP', 'Водяной чип', 'Это водяной чип производства компании Vault-Tec. Стандартная упаковка содержит пять штук.', 'Разное', '', 'water', 907, 1),
(56, 'PID_DOG_TAGS', 'Солдатские жетоны', 'Армейские жетоны. Вы можете прочесть имя \"Даркуотер\", но больше ничего разобрать нельзя.', 'Разное', '', 'tag', 0, 50),
(57, 'PID_ELECTRONIC_BUG', 'Жучок', 'Миниатюрный микрофон и передающее устройство.', 'Разное', '', 'bug', 97, 50),
(58, 'PID_HOLODISK', 'Пленка', 'Пленка голографического диска от компании Ватт Электроникс. Кажется, конкретно эта пленка в очень плохом состоянии.', 'Разное', '', 'tape3', 25000, 40),
(59, 'PID_MOTION_SENSOR', 'Сенсор движения', 'Сенсор движения Ватт Электроникс, модель \"Ви-ДОК\". Фиксирует движение биосуществ на определенном расстоянии в метрах при помощи датчика. Наличие такого сенсора в вашем снаряжении поможет избегать нежелательных встреч за городом (+20% к скорости).', 'Доп слот', '', 'motion', 3175, 800),
